import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-empleado',
  templateUrl: './empleado.component.html',
  styleUrls: ['./empleado.component.css']
})
export class EmpleadoComponent implements OnInit{

  nombre = "Juan";
  apellido = "Díaz";
  private edad = 18;

  empresa = "";

  habilitacionCuadro = false;

  getEdad(){
    return this.edad;
  }

  constructor(){

  }

  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

  llamarEmpresa(nombreEmpresa: string): void{
    this.empresa = nombreEmpresa;
  }


}
<p>Nombre del empleado: {{nombre}}</p>
<p>Apellido del empleado: {{apellido}}</p>
<p>Edad del empleado: {{getEdad() >= 18?'Mayor de edad':'Menor de edad'}}</p>
<p>Empresa del empleado: {{empresa.value}}</p>

<br>

<p>Introduce el nombre de la empresa:  <input type="text" #empresa (keyup)="0" [disabled]="habilitacionCuadro"> </p>

<br>
<br>

<p>Registrado: <input type="checkbox" id="registro" [checked]="usuRegistrado"></p>

